import { trigger, transition, animate, keyframes, style } from '@angular/animations';
import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-basket-btn',
  templateUrl: './basket-btn.component.html',
  styleUrls: ['./basket-btn.component.scss'],
  animations: [
    trigger('skewZoom', [
      transition(':increment', [
        animate("0.5s ease", keyframes([
          style({ transform: "rotate(0deg) scale(1)" }), // offset = 0
          style({ transform: "rotate(24deg) scale(1.2)" }), // offset = 0.5
          style({ transform: "rotate(0deg) scale(1)" }), // offset = 1
        ]))
      ])
    ]),
  ]
})
export class BasketBtnComponent implements OnInit {
  shoppingCount$: Observable<number> = Of(2);
  constructor() { }

  ngOnInit(): void {
  }

}
function Of(arg0: number): Observable<number> {
  throw new Error('Function not implemented.');
}

<button [routerLink]="['basket']"
        class="d-none d-lg-inline-flex btn btn-primary basket-btn position-relative justify-content-center align-items-center border-0 p-1 shadow-none">
  <span class="me-2 ps-2 text-right">
    <div class="order-total">
      Votre panier
    </div>
  </span>

  <em appFeatherIcons data-feather="shopping-cart" class="ms-2 me-2"></em>
  <span *ngIf="(shoppingCount$ | async) ?? 0 as shoppingCount" [@skewZoom]="shoppingCount"
        class="badge bg-accent rounded-pill cart-count">
    {{ shoppingCount }}</span>
</button>

